// Process call report
const report = $input.item.json.message;
const callId = report.call.id;
const duration = report.call.duration;
const status = report.call.status;
const transcript = report.call.transcript || [];
const summary = report.call.summary || '';
const metadata = report.call.metadata || {};

// Extract key information from transcript
let customerSentiment = 'neutral';
let isInterested = false;
let nextAction = 'none';
let productsDiscussed = [];

// Analyze transcript for sentiment and actions
if (transcript.length > 0) {
  const fullTranscript = transcript.map(t => t.text).join(' ');

  // Check for positive indicators
  const positiveWords = ['à¤¹à¤¾à¤‚', 'à¤œà¤°à¥‚à¤°', 'à¤šà¤¾à¤¹à¤¿à¤', 'à¤¬à¤¤à¤¾à¤‡à¤', 'à¤•à¤¿à¤¤à¤¨à¤¾', 'à¤‘à¤°à¥à¤¡à¤°', 'à¤–à¤°à¥€à¤¦à¤¨à¤¾'];
  const negativeWords = ['à¤¨à¤¹à¥€à¤‚', 'à¤¬à¤¾à¤¦ à¤®à¥‡à¤‚', 'à¤¸à¥‹à¤šà¥‡à¤‚à¤—à¥‡', 'à¤œà¤°à¥‚à¤°à¤¤ à¤¨à¤¹à¥€à¤‚', 'à¤®à¤¹à¤‚à¤—à¤¾'];

  const positiveCount = positiveWords.filter(word => fullTranscript.includes(word)).length;
  const negativeCount = negativeWords.filter(word => fullTranscript.includes(word)).length;

  if (positiveCount > negativeCount) {
    customerSentiment = 'positive';
    isInterested = true;
  } else if (negativeCount > positiveCount) {
    customerSentiment = 'negative';
  }

  // Extract products mentioned
  const products = ['TMT', 'rod', 'sheet', 'pipe', 'angle', 'channel'];
  productsDiscussed = products.filter(product => fullTranscript.toLowerCase().includes(product));
}

// Determine next action
if (summary.includes('order') || summary.includes('à¤‘à¤°à¥à¤¡à¤°')) {
  nextAction = 'order_placed';
} else if (isInterested) {
  nextAction = 'follow_up_required';
} else if (customerSentiment === 'negative') {
  nextAction = 'not_interested';
}

return {
  callId,
  customerId: metadata.customerId,
  customerName: metadata.customerName,
  callDate: new Date().toISOString(),
  duration,
  status,
  customerSentiment,
  isInterested,
  productsDiscussed: productsDiscussed.join(', '),
  nextAction,
  summary,
  agentPerformance: report.call.analysis?.agentEvaluation || 'good'
};

// Generate daily report
const calls = $input.all();
const staticData = $getWorkflowStaticData('global');
const currentMonth = DateTime.now().toFormat('yyyy-MM');
const monthlyCount = staticData.monthlyCallCount?.[currentMonth] || 0;

const totalCalls = calls.length;
const successfulCalls = calls.filter(c => c.json.status === 'completed').length;
const interestedCustomers = calls.filter(c => c.json.isInterested).length;
const ordersPlaced = calls.filter(c => c.json.nextAction === 'order_placed').length;
const followUpsRequired = calls.filter(c => c.json.nextAction === 'follow_up_required').length;

const avgDuration = calls.reduce((sum, c) => sum + (c.json.duration || 0), 0) / totalCalls || 0;

const productsDiscussed = {};
calls.forEach(c => {
  if (c.json.productsDiscussed) {
    c.json.productsDiscussed.split(', ').forEach(product => {
      productsDiscussed[product] = (productsDiscussed[product] || 0) + 1;
    });
  }
});

const report = `Daily Cold Call Report - ${new Date().toLocaleDateString('hi-IN')}

Monthly Progress: ${monthlyCount}/50 calls completed

Today's Statistics:
- Total Calls Made: ${totalCalls}
- Successful Calls: ${successfulCalls}
- Failed/No Answer: ${totalCalls - successfulCalls}
- Average Call Duration: ${Math.round(avgDuration / 60)} minutes

Customer Response:
- Interested Customers: ${interestedCustomers}
- Orders Placed: ${ordersPlaced}
- Follow-ups Required: ${followUpsRequired}
- Not Interested: ${calls.filter(c => c.json.nextAction === 'not_interested').length}

Products Discussed:
${Object.entries(productsDiscussed).map(([product, count]) => `- ${product}: ${count} times`).join('\n')}

Performance Summary:
- Success Rate: ${Math.round((successfulCalls / totalCalls) * 100)}%
- Interest Rate: ${Math.round((interestedCustomers / successfulCalls) * 100)}%
- Conversion Rate: ${Math.round((ordersPlaced / totalCalls) * 100)}%

Top Performing Calls:
${calls
  .filter(c => c.json.nextAction === 'order_placed')
  .slice(0, 3)
  .map(c => `- ${c.json.customerName} (${c.json.businessType})`)
  .join('\n') || '- No orders placed today'}
`;

return {
  report,
  stats: {
    totalCalls,
    successfulCalls,
    interestedCustomers,
    ordersPlaced,
    followUpsRequired,
    monthlyCount
  }
};

